mapper.update:
update basket
set product_cnt = product_cnt + #{productCnt}
where basket_no = #{basketNo}

mapper.select:
  	select p.product_name, pi.product_color, pi.product_size, p.product_price, pi.product_info_no
	from product p left join product_info pi on (p.product_no = pi.product_no)
	where p.product_no = #{productInfoNo}
