mapper.select:
  	select basket_no, user_id,  pi.product_color, pi.product_size, pi.product_info_no, product_cnt, product_img, product_name, product_price, p.product_no
	from basket b right join product_info pi on ( b.product_info_no = pi.product_info_no) right join product p on (pi.product_no = p.product_no ) 
  	where user_id = #{userId}

mapper.update:
update basket
set product_cnt = #{productCnt}
where basket_no = #{basketNo}
