mapper.update:
update qna
set post_title = #{postTitle},
    content = #{content}
where post_no = #{postNo}
and product_no = #{productNo}

mapper.delete:
delete from qna
where post_no = #{postNo}
and product_no = #{productNo}

mapper.update:
update qna
set views = views + 1
where post_no = #{postNo}

mapper.select:
select count(*)
from qna
where product_no = #{productNo}
