mapper.select:
select * 
from category 
where category_no_ref = #{categoryNo} 
or category_no = #{categoryNo}

select r.* from (select rownum rn, a.* from (select * from product b order by product_no) a
where rownum <= #{page}*8)r
where r.rn > (#{page}-1) *8 and category_no = #{categoryNo}

select count(*)
from product
where category_no = #{categoryNo}

		select r.*
		from (select rownum rn, a.*
      		  from (select *
            	    from product b) a
              	    
              where rownum <= #{page}*6
              and product_name like '%'|| #{keyword} || '%')r
              
        where r.rn > (#{page}-1) *6
		and product_name like '%'|| #{keyword} || '%'
		
			
			and r.category_no in (select category_no 
		                      from category 
                              where category_no_ref = #{categoryNo}
	            
	            	
	            			
	                    	and product_price >= #{productPrice1}
	                	
	            	
	            	
	            			
	                    	and product_price <= #{productPrice2}
	                	
	            	
	            
            )                                                                                     
			union all
			select  r.*
			from (select rownum rn, a.*
      		  	  from (select *
            			from product b) a 
              	 	   
      		  	  where rownum <= #{page}*6
      		  	  and product_name like '%'|| #{keyword} || '%' 	   
				  and category_no = #{categoryNo})r
      		   	  
      		where r.rn > (#{page}-1) *6
      		and product_name like '%'|| #{keyword} || '%' 	   
			and category_no = #{categoryNo}
				
	            	
	            			
	                    	and product_price >= #{productPrice1}
	                	
	            	
	            	
	            			
	                    	and product_price <= #{productPrice2}
	                	
	            	
	            
			
			
				
						
                    	and product_price >= #{productPrice1}
                	
				
				
						
                        and product_price <= #{productPrice2}

		select count(*)
		from (select *
			  from product p
			  where product_name like '%'|| #{keyword} || '%'
		
			
				and p.category_no in (select category_no 
			                            from category 
	                                    where category_no_ref = #{categoryNo}
		            
		            	
		            			
		                    	and product_price >= #{productPrice1}
		                	
		            	
		            	
		            			
		                    	and product_price <= #{productPrice2}
		                	
		            	
		            
	            )                        
	        	union all
	        	select *
				from product p
				where product_name like '%'|| #{keyword} || '%'
				and category_no = #{categoryNo}
				
	            	
	            			
	                    	and product_price >= #{productPrice1}
	                	
	            	
	            	
	            			
	                    	and product_price <= #{productPrice2}
	                	
	            	
	            
	            )
			
			
				
						
                    	and product_price >= #{productPrice1}
                	
				
				
						
                        and product_price <= #{productPrice2}
                     
				
				)
