mapper.select:
    select p.product_name, p.product_price, pi.product_color, pi.product_size, b.product_cnt, pi.product_info_no
	from product_info pi left join product p on (pi.product_no = p.product_no)
                         left join basket b on (pi.product_info_no = b.product_info_no)
	where b.user_id = #{userId}

  	select p.product_no, p.product_name, p.product_price, pi.product_color, pi.product_size, pi.product_info_no
	from product_info pi left join product p on (pi.product_no = p.product_no)
	where pi.product_info_no = #{productInfoNo}

mapper.delete:
delete from basket
where product_info_no = #{productInfoNo}
and user_id = #{userId}
